declare(strict_types=1);

namespace think\route\dispatch;

use think\route\Dispatch;

/**
 * Callback Dispatcher
 */
class Callback extends Dispatch
{
    public function exec()
    {
        // 执行回调方法
        $vars = $this->getActionBindVars();

        return $this->app->invoke($this->dispatch, $vars);
    }
}
